import React from 'react';
import './App.css';

class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            value: 'default item',
            show: true,
            data: [{
                    id: 1,
                    title: 'First Item'
                },
                {
                    id: 2,
                    title: 'Second Item'
                },
                {
                    id: 3,
                    title: 'Third Item'
                },
            ]
        }
    }
    changeTitle(title) {
        this.setState({
            value: title
        })
    }
}

export default App;
